mapper.insert:
CREATE DATABASE IF NOT EXISTS `codedriver_${tenantUuid}` character set utf8mb4 COLLATE utf8mb4_general_ci;
DROP USER IF EXISTS '${tenantUuid}'@'%';
CREATE USER '${tenantUuid}'@'%' IDENTIFIED BY '${passwordPlain}';
GRANT ALL PRIVILEGES ON `codedriver_${tenantUuid}`.* to '${tenantUuid}'@'%';
CREATE DATABASE IF NOT EXISTS `codedriver_${tenantUuid}_data` character set utf8mb4 COLLATE utf8mb4_general_ci;
GRANT ALL PRIVILEGES ON `codedriver_${tenantUuid}_data`.* to '${tenantUuid}'@'%';
