mapper.select:
SELECT a.`uuid`, a.`name`, a.`lft`, a.`parent_uuid` as `parentUuid`, d.`id` as `tagId`, d.`name` as `tagName`, (SELECT COUNT(1) FROM `user_team` e WHERE e.`team_uuid` = a.`uuid`) AS `userCount`, (SELECT COUNT(1) FROM `team` WHERE `parent_uuid` = a.`uuid`) as `childCount` FROM ( SELECT * FROM `team` WHERE `uuid` != '0'
and `name` LIKE CONCAT('%', #{keyword}, '%')
and `parent_uuid` = #{parentUuid}
ORDER BY `lft`
LIMIT #{startNum}, #{pageSize}
) a LEFT JOIN `user_team` b ON a.`uuid` = b.`team_uuid` LEFT JOIN `team_tag` c ON a.`uuid` = c.`team_uuid` LEFT JOIN `tag` d ON c.`tag_id` = d.`id` ORDER BY a.`lft`

SELECT COUNT(1) FROM `team` WHERE 1=1
and name LIKE CONCAT('%', #{keyword}, '%')
and parent_uuid = #{parentUuid}

SELECT a.`uuid`, a.`name`, a.`lft`, a.`parent_uuid` as `parentUuid`, c.`id` as `tagId`, c.`name` as `tagName` FROM `team` a LEFT JOIN `team_tag` b ON a.`uuid` = b.`team_uuid` LEFT JOIN `tag` c ON b.`tag_id` = c.`id`
AND a.`uuid` = #{uuid}
AND a.`name` = #{name}

SELECT b.`user_uuid` FROM `team` a
JOIN `user_team` b ON b.`team_uuid` = a.`uuid` AND b.`title`=#{title}
JOIN `user` c ON c.`uuid`=b.`user_uuid` AND c.`is_active`=1
WHERE a.`level` = #{level} AND a.`lft` <= #{lft} AND a.`rht` >= #{rht}

SELECT a.`uuid`, COUNT(DISTINCT b.`user_uuid`) AS userCount, COUNT(DISTINCT c.`uuid`) AS childCount FROM `team` a LEFT JOIN `user_team` b ON b.`team_uuid` = a.`uuid` LEFT JOIN `team` c ON c.`parent_uuid` = a.`uuid` WHERE a.`uuid` IN
#{teamUuid}
GROUP BY a.`uuid`
