mapper.select:
SELECT
count(1)
FROM
`user_team`
WHERE user_id = #{userId}
AND team_uuid = #{teamUuid}

SELECT a.`user_id` as userId, a.`user_name` as userName, a.`email`, a.`phone`, e.`password`, a.`pinyin`, a.`is_active` as isActive, b.role_name as roleName, c.team_uuid as teamUuid, d.name as teamName FROM `user` a LEFT JOIN user_role b ON a.user_id = b.user_id LEFT JOIN user_team c ON a.user_id = c.user_id LEFT JOIN team d ON c.team_uuid = d.uuid LEFT JOIN `user_password` e ON a.`user_id` = e.`user_id` LEFT JOIN `user_authority` f ON a.`user_id` = f.`user_id` WHERE 1=1
AND (a.user_id LIKE CONCAT(#{keyword}, '%') OR a.user_name LIKE CONCAT(#{keyword}, '%'))
AND c.`team_uuid` = #{teamUuid}
AND c.`module` = #{authModule}
AND c.`auth` = #{auth}
ORDER BY a.user_id
LIMIT #{startNum}, #{pageSize}

SELECT count(1) FROM `user` a LEFT JOIN `user_team` b ON a.`user_id` = b.`user_id` LEFT JOIN `user_authority` c ON a.`user_id` = c.`user_id` WHERE 1=1
AND (a.`user_id` LIKE CONCAT(#{keyword}, '%') OR a.`user_name` LIKE CONCAT(#{keyword}, '%'))
AND b.`team_uuid` = #{teamUuid}
AND c.`module` = #{authModule}
AND c.`auth` = #{auth}

mapper.delete:
