mapper.select:
SELECT `target_id`,`target_field`,`word`,`start`,`end` FROM `fulltextindex_field_${moduleId}` ftf LEFT JOIN `fulltextindex_offset_${moduleId}` fto ON ftf.id = fto.`field_id` LEFT JOIN `fulltextindex_word` ftw ON ftw.`id` = ftf.`word_id` WHERE target_id IN #{targetId}
AND word IN #{k}
ORDER BY ftf.`counter` DESC

SELECT target_id AS targetId, target_type AS targetType, target_field AS targetField, content
FROM `fulltextindex_content_${moduleId}`
WHERE target_id = #{targetId}
