mapper.select:
SELECT
`token`
FROM `api`
WHERE 1=1

    AND `module_id` = #{moduleId}


    AND `handler` = #{handler}


    AND `is_active` = #{isActive}


    AND (`name` LIKE CONCAT('%', #{keyword}, '%') OR `token` LIKE CONCAT('%', #{keyword}, '%'))

SELECT `name`, `module_id` AS moduleId, `handler`, `username`, `password`, `config`, `is_active` AS isActive, `token`, `expire`, `description`, `authtype`, `type`, `qps`, `timeout`, `need_audit` AS needAudit FROM `api`
and module_id in #{item}
order by `create_time` desc
