mapper.select:
SELECT `id`, `uuid`, `name`, `lft`, `rht`, `parent_uuid` AS `parentUuid`, `level` FROM `team`
and `name` LIKE CONCAT('%', #{keyword}, '%')
and `parent_uuid` = #{parentUuid}
and `level` = #{level}
and `is_delete` = #{isDelete}
and (
uuid in
#{teamUuid}
or
`upward_uuid_path` LIKE CONCAT('%',#{parentTeamUuid},'%')
)
ORDER BY `lft`
LIMIT #{startNum}, #{pageSize}

SELECT `id`, `uuid`, `name`, `lft`, `rht`, `parent_uuid` AS `parentUuid`, `level`, `upward_name_path` AS upwardNamePath FROM `team`
and (`name` LIKE CONCAT('%', #{keyword}, '%') or `uuid` = #{keyword})
and `parent_uuid` = #{parentUuid}
and `level` = #{level}
and `is_delete` = #{isDelete}
ORDER BY LENGTH(`name`)
LIMIT #{startNum}, #{pageSize}

SELECT
COUNT(1)
FROM
`team`


        and (`name` LIKE CONCAT('%', #{keyword}, '%') or `uuid` = #{keyword})


        and `parent_uuid` = #{parentUuid}


        and `level` = #{level}


        and `is_delete` = #{isDelete}

SELECT a.`id`, a.`uuid`, a.`name`, a.`lft`, a.`rht`, a.`parent_uuid` as `parentUuid`, a.`level`, a.`upward_uuid_path` AS upwardUuidPath, a.`upward_name_path` AS upwardNamePath FROM `team` a
AND a.`uuid` = #{uuid}
AND a.`name` = #{name}
and `is_delete` = #{isDelete}

SELECT a.`id`, a.`uuid`, a.`name`, a.`is_delete` AS isDelete FROM `team` a
AND a.`uuid` = #{uuid}
and `is_delete` = #{isDelete}

SELECT COUNT(1)
FROM `team`
WHERE `name` = #{name}
  AND `parent_uuid` = #{parentUuid}
  AND `uuid` != #{uuid}
  AND `is_delete` = 0

SELECT distinct `uuid` FROM `team` WHERE `uuid` in
#{item}
and `is_delete` = 0

SELECT a.`id`, a.`uuid`, a.`name`, a.`parent_uuid` AS parentUuid, a.`lft`, a.`rht`, a.`level` FROM `team` a WHERE `level` in
#{item}
and (
a.`lft` <= #{team.lft} and a.`rht` >= #{team.rht}
) and a.`is_delete` = 0

SELECT a.`id`, a.`uuid`, a.`name`, a.`parent_uuid` AS parentUuid, a.`lft`, a.`rht`, a.`level` FROM `team` a WHERE
a.`lft` >= #{team.lft} and a.`rht` <= #{team.rht}
and a.`is_delete` = 0

SELECT COUNT(1)
FROM `user_team`
WHERE `team_uuid` = #{value}

SELECT `id`, `uuid`, `name`, `parent_uuid` AS parentUuid, `level`, `lft`, `rht`, `upward_uuid_path` AS upwardUuidPath, `upward_name_path` AS upwardNamePath FROM `team` WHERE `uuid` in
#{item}
and `is_delete` = 0

SELECT `id`, `uuid`, `name`, `parent_uuid` AS parentUuid, `level`, `lft`, `rht`, `upward_uuid_path` AS upwardUuidPath, `upward_name_path` AS upwardNamePath FROM `team` WHERE `id` in
#{id}
and `is_delete` = 0

SELECT a.`id`, a.`uuid`, a.`name`, a.`parent_uuid` AS parentUuid, a.`lft`, a.`rht`, a.`level`, 0 as `childCount` FROM `team` a WHERE a.`lft` <= #{lft} AND a.`rht` >= #{rht} and a.`is_delete` = 0
AND a.`level` = #{level}
ORDER BY `lft` ASC

SELECT a.`uuid`, COUNT(b.`user_uuid`) AS userCount FROM `team` a LEFT JOIN `user_team` b ON b.`team_uuid` = a.`uuid` LEFT JOIN `user` c ON b.`user_uuid` = c.`uuid`
and c.`is_active` = #{isActive}
WHERE a.`is_delete` = 0 and a.`uuid` IN
#{teamUuid}
GROUP BY a.`uuid`

SELECT a.`uuid`, COUNT(DISTINCT b.`uuid`) AS childCount FROM `team` a LEFT JOIN `team` b ON b.`parent_uuid` = a.`uuid` and b.`is_delete` = 0 WHERE a.`is_delete` = 0 and a.`uuid` IN
#{teamUuid}
GROUP BY a.`uuid`

select `name`, count(1) as `nameRepeatCount` from `team` where `name` in
#{item}
and `is_delete` = 0 group by `name`

SELECT `uuid`
FROM `team`
WHERE `lft` > #{lft}
  AND `rht` < #{rht}
  and `is_delete` = 0

select count(title_id)
from `team_user_title`
where `title_id` = #{value}

mapper.update:
UPDATE `team` d
JOIN (
SELECT b.`uuid` AS `uuid`, GROUP_CONCAT(a.`uuid` ORDER BY a.`lft`) AS upward_uuid_path
FROM `team` a
JOIN `team` b ON a.`lft` <= b.`lft` AND a.`rht` >= b.`rht`


        AND b.`lft` >= #{lft}


        AND b.`rht` <= #{rht}


GROUP BY b.`uuid`
) c ON c.`uuid` = d.`uuid`
SET d.`upward_uuid_path` = c.`upward_uuid_path`

UPDATE `team` d
JOIN (
SELECT b.`uuid` AS `uuid`, GROUP_CONCAT(a.`name` ORDER BY a.`lft` SEPARATOR '/') AS upward_name_path
FROM `team` a
JOIN `team` b ON a.`lft` <= b.`lft` AND a.`rht` >= b.`rht`


        AND b.`lft` >= #{lft}


        AND b.`rht` <= #{rht}


GROUP BY b.`uuid`
) c ON c.`uuid` = d.`uuid`
SET d.`upward_name_path` = c.`upward_name_path`
