mapper.select:
SELECT `id`, `name`, `parent_id` as parentId, `worktime_uuid` as worktimeUuid, `is_active` as isActive, `upward_id_path` AS upwardIdPath, `upward_name_path` AS upwardNamePath, `lft`, `rht` FROM `region` WHERE `lft` >= #{lft} AND `rht` <= #{rht}
AND `is_active` = #{isActive}
ORDER BY `lft`

SELECT `id`, `name`, `parent_id` as parentId, `worktime_uuid` as worktimeUuid, `is_active` as isActive, `upward_id_path` AS upwardIdPath, `upward_name_path` AS upwardNamePath, `lft`, `rht` FROM `region`
`name` LIKE CONCAT('%',#{keyword},'%')
`id` in
#{id}
ORDER BY `id` LIMIT #{startNum}, #{pageSize}

SELECT r.`id` FROM `region_team` rt join `region` r on rt.region_id = r.id join `team` t on rt.`team_uuid` = t.`uuid` where rt.`team_uuid` in
#{teamUuid}
and r.`is_active` = 1

SELECT r.`id` FROM `region_team` rt join `region` r on rt.region_id = r.id join `team` t on rt.`team_uuid` = t.`uuid` where rt.team_uuid in
#{teamUuid}
and rt.checked_children = #{checkedChildren} and r.`is_active` = 1

mapper.update:
UPDATE `region` d
JOIN (
SELECT b.`id` AS `id`, GROUP_CONCAT(a.`id` ORDER BY a.`lft`) AS upward_id_path
FROM `region` a
JOIN `region` b ON a.`lft` <= b.`lft` AND a.`rht` >= b.`rht`


        AND b.`lft` >= #{lft}


        AND b.`rht` <= #{rht}


GROUP BY b.`id`
) c ON c.`id` = d.`id`
SET d.`upward_id_path` = c.`upward_id_path`

UPDATE `region` d
JOIN (
SELECT b.`id` AS `id`, GROUP_CONCAT(a.`name` ORDER BY a.`lft` SEPARATOR '/') AS upward_name_path
FROM `region` a
JOIN `region` b ON a.`lft` <= b.`lft` AND a.`rht` >= b.`rht`


        AND b.`lft` >= #{lft}


        AND b.`rht` <= #{rht}


GROUP BY b.`id`
) c ON c.`id` = d.`id`
SET d.`upward_name_path` = c.`upward_name_path`
